mapper.insert:

	SELECT RCV_SEQ_NO.CURRVAL FROM DUAL

INSERT INTO RCVAPPL (
	SSN 
	, RCV_D 
	, RCV_SEQ_NO 
	, APPL_D 
	, BIRTH_D 
	, HG_NM 
	, ENG_NM 
	, STL_MTD 
	, STL_ACT 
	, BNK_CD 
	, STL_DD
	, MGT_BBRN
	, APPL_CLAS
	, STMT_SND_MTD
	, BILLADR_ADR1
	, BILLADR_ADR2
	, BILLADR_ZIP
	, HDP_NO
	, BRD
	, SCRT_NO
	, EMAIL_ADR
	, CRD_NO
	, IMPSB_CLAS
	, IMPSB_CD
	, LST_OPR_TM
	, LST_OPR_D
	, LST_OPRT_EMPNO
) VALUES ( 
	#{ssn}
	, TO_CHAR(sysdate, 'YYYYMMDD')
	, RCV_SEQ_NO.NEXTVAL 
	, #{applD} 
	, #{birthD} 
	, #{hgNm} 
	, #{engNm} 
	, #{stlMtd} 
	, #{stlAct} 
	, #{bnkCd}
	, #{stlDd}
	, #{mgtBbrn}
	, #{applClas}
	, #{stmtSndMtd}
	, #{billadrAdr1}
	, #{billadrAdr2}
	, #{billadrZip}
	, #{hdpNo}
	, #{brd}
	, #{scrtNo}
	, #{emailAdr}
	, #{crdNo}
	, #{impsbClas}
	, #{impsbCd}
	, #{lstOprTm}
	, TO_CHAR(sysdate, 'YYYYMMDD')
	, #{lstOprtEmpno}
)


	SELECT CUST_NO.CURRVAL FROM DUAL

INSERT INTO CUST (
	CUST_NO
	, SSN
	, REG_D
	, HG_NM
	, BIRTH_D
	, HDP_NO
	, LST_OPR_TM
	, LST_OPR_D
	, LST_OPRT_EMPNO
) VALUES (
	LPAD(CUST_NO.NEXTVAL,9,0)
	, #{ssn}
	, #{regD}
	, #{hgNm}
	, #{birthD}
	, #{hdpNo}
	, #{lstOprTm}
	, TO_CHAR(sysdate, 'YYYYMMDD')
	, #{lstOprtEmpno}
)

INSERT INTO BILL (
	CUST_NO
	, STL_ACT
	, BNK_CD
	, DPS_NM
	, STL_MTD
	, STL_DD
	, PRCS_CLAS
	, STMT_SND_MTD
	, STMT_DENI_CLAS
	, BILL_ZIP
	, BILL_ADR1
	, BILL_ADR2
	, EMAIL_ADR
	, LST_OPR_TM
	, LST_OPR_D
	, LST_OPRT_EMPNO
) VALUES (
	#{custNo}
	, #{stlAct}
	, #{bnkCd}
	, #{dpsNm}
	, #{stlMtd}
	, #{stlDd}
	, #{prcsClas}
	, #{stmtSndMtd}
	, #{stmtDeniClas}
	, #{billZip}
	, #{billAdr1}
	, #{billAdr2}
	, #{emailAdr}
	, #{lstOprTm}
	, TO_CHAR(sysdate, 'YYYYMMDD')
	, #{lstOprtEmpno}
)


	SELECT
		#{crdNo} || LPAD(CRD_NO.CURRVAL,9,0) || '0' as crdNo
	FROM DUAL

INSERT INTO CRD (
	CRD_NO
	, CUST_NO
	, MGT_BBRN
	, REG_D
	, SSN
	, VLD_DUR
	, BRD
	, SCRT_NO
	, ENG_NM
	, BF_CRD_NO
	, LST_CRD_F
	, FST_REG_D
	, CRD_GRD
	, LST_OPR_TM
	, LST_OPR_D
	, LST_OPRT_EMPNO
) VALUES (
	#{crdNo} || LPAD(CRD_NO.NEXTVAL,9,0) || '0'
	, #{custNo}
	, #{mgtBbrn}
	, TO_CHAR(sysdate, 'YYYYMMDD')
	, #{ssn}
	, #{vldDur}
	, #{brd}
	, #{scrtNo}
	, #{engNm}
	, #{bfCrdNo}
	, #{lstCrdF}
	, TO_CHAR(sysdate, 'YYYYMMDD')
	, #{crdGrd}
	, #{lstOprTm}
	, TO_CHAR(sysdate, 'YYYYMMDD')
	, #{lstOprtEmpno}
)

mapper.select:
SELECT
	COUNT(RCV_SEQ_NO)
FROM RCVAPPL
WHERE SSN = #{ssn}
AND RCV_D = #{rcvD}
AND APPL_CLAS = #{applClas}
AND BRD = #{brd}
AND IMPSB_CLAS IS NULL

SELECT T.* FROM ( SELECT SSN , RCV_D , RCV_SEQ_NO , APPL_D , BIRTH_D , HG_NM , ENG_NM , STL_MTD , STL_ACT , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE RC.BNK_CD = CD.CODE AND CD.GROUP_CD = 'C003') AS BNK_CD , STL_DD , MGT_BBRN , APPL_CLAS , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE RC.STMT_SND_MTD = CD.CODE AND CD.GROUP_CD = 'C006') AS STMT_SND_MTD , BILLADR_ADR1 , BILLADR_ADR2 , BILLADR_ZIP , HDP_NO , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE RC.BRD = CD.CODE AND CD.GROUP_CD = 'C004') AS BRD , SCRT_NO , EMAIL_ADR , CRD_NO , DECODE(RC.IMPSB_CLAS, '1', '불능', '유효') as IMPSB_CLAS , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE RC.STMT_SND_MTD = CD.CODE AND CD.GROUP_CD = 'C001') AS IMPSB_CD , LST_OPR_TM , LST_OPR_D , LST_OPRT_EMPNO FROM RCVAPPL RC
SSN = #{ssn}
AND RCV_D = #{rcvD}
AND RCV_SEQ_NO = #{rcvSeqNo}
ORDER BY RCV_D DESC ) T WHERE ROWNUM = 1

SELECT RCV_D , RCV_SEQ_NO , SSN , HG_NM , ENG_NM , APPL_CLAS , BRD , HDP_NO , IMPSB_CLAS , IMPSB_CD FROM RCVAPPL
RCV_D BETWEEN TO_DATE(#{startRcvD}, 'YYYYMMDD') AND TO_DATE(#{endRcvD} || ' 23:59:59', 'YYYYMMDD HH24:MI:SS')
AND APPL_CLAS = #{applClas}
AND SSN = #{ssn}

SELECT RC.HG_NM , RC.HDP_NO , RC.RCV_D , RC.STL_MTD , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE RC.STL_MTD = CD.CODE AND CD.GROUP_CD = 'C002') AS STL_MTD_NM , RC.BNK_CD , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE RC.BNK_CD = CD.CODE AND CD.GROUP_CD = 'C003') AS BNK_CD_NM , RC.STL_ACT , RC.STL_DD , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE RC.STL_DD = CD.CODE AND CD.GROUP_CD = 'C009') AS STL_DD_NM , RC.STMT_SND_MTD , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE RC.STMT_SND_MTD = CD.CODE AND CD.GROUP_CD = 'C006') AS STMT_SND_MTD_NM , RC.EMAIL_ADR , RC.BILLADR_ZIP , CR.CRD_NO , CR.ENG_NM , CR.VLD_DUR , CR.BRD , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE CR.BRD = CD.CODE AND CD.GROUP_CD = 'C004') AS BRD_NM , CR.CRD_GRD , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE CR.CRD_GRD = CD.CODE AND CD.GROUP_CD = 'C005') AS CRD_GRD_NM , CR.FST_REG_D , CR.BF_CRD_NO , DECODE(CR.LST_CRD_F, '1', 'O', 'X') as LST_CRD_F , CR.MGT_BBRN FROM CRD CR INNER JOIN RCVAPPL RC ON CR.CRD_NO = RC.CRD_NO
CR.SSN = #{ssn}
AND CR.CRD_NO = #{crdNo}

SELECT CR.CRD_NO , CR.CUST_NO , CR.MGT_BBRN , CR.REG_D , CR.SSN , CR.VLD_DUR , CR.BRD , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE CR.BRD = CD.CODE AND CD.GROUP_CD = 'C004') AS BRD_NM , CR.SCRT_NO , CR.ENG_NM , CR.BF_CRD_NO , CR.LST_CRD_F , CR.FST_REG_D , CR.CRD_GRD , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE CR.CRD_GRD = CD.CODE AND CD.GROUP_CD = 'C005') AS CRD_GRD_NM , CR.LST_OPR_TM , CR.LST_OPR_D , CR.LST_OPRT_EMPNO FROM CRD CR
SSN = #{ssn}
AND CRD_NO = #{crdNo}

mapper.update:
UPDATE RCVAPPL SET
	CRD_NO = #{crdNo}
WHERE CRD_NO IS NULL
AND SSN = #{ssn}
AND SCRT_NO = #{scrtNo}
AND BRD = #{brd}
AND RCV_SEQ_NO = #{rcvSeqNo}

mapper.select:
SELECT CU.SSN , CU.HG_NM , CU.BIRTH_D , CU.HDP_NO , BI.BNK_CD , (SELECT CODE_NM FROM COMM_CODE_DTL CD WHERE BI.BNK_CD = CD.CODE AND CD.GROUP_CD = 'C003') AS BNK_CD_NM , BI.STL_ACT , BI.BILL_ADR1 || BI.BILL_ADR2 AS BILL_ADR1 FROM CUST CU INNER JOIN BILL BI ON CU.CUST_NO = BI.CUST_NO
CU.HG_NM = #{hgNm}
AND CU.BIRTH_D = #{birthD}
AND CU.HDP_NO = #{hdpNo}
