mapper.select:
SELECT T.CODE_NAME , T.BOARD_NUM , T.BOARD_TITLE FROM ( SELECT ROWNUM RN , C.CODE_NAME , B.BOARD_NUM , B.BOARD_TITLE FROM BOARD B INNER JOIN COM_CODE C ON B.BOARD_TYPE = C.CODE_ID AND C.CODE_TYPE = 'menu'
C.CODE_ID = #{searchCode}
AND ROWNUM <= #{pageNum} * #{amount} ) T
WHERE T.RN > (#{pageNum} - 1) * #{amount}

mapper.insert:
INSERT INTO BOARD (
	BOARD_TYPE 
	, BOARD_NUM 
	, BOARD_TITLE 
	, BOARD_COMMENT 
	, CREATOR 
	, CREATE_TIME 
	, FILE_ROOT 
) VALUES (
	#{boardType} 
	, BOARD_SEQ.NEXTVAL 
	, #{boardTitle} 
	, #{boardComment} 
	, #{creator} 
	, TO_CHAR(sysdate, 'YYYY-MM-DD') 
	, #{fileRoot} 
	)

mapper.update:

UPDATE BOARD SET 
	BOARD_TYPE = #{boardType} 
	, BOARD_TITLE = #{boardTitle} 
	, BOARD_COMMENT = #{boardComment} 
	, MODIFIER = #{modifier} 
	, MODIFIED_TIME = TO_CHAR(sysdate, 'YYYY-MM-DD')
WHERE CREATOR = #{modifier} 
AND BOARD_NUM = #{boardNum}
